set -o errexit
set -o nounset
set -o pipefail

declare BUILD_VERSION IMAGE_HASH KUBECTL_DIRECTPV OPERATOR_SDK YQ
PACKAGE=minio-directpv-operator-rhmp

function get_operator_sdk() {
    curl --silent --location --insecure --fail https://mirror.openshift.com/pub/openshift-v4/x86_64/clients/operator-sdk/4.13.1/operator-sdk-v1.28.0-ocp-linux-x86_64.tar.gz | tar --strip-component 2 -zxf - ./x86_64/operator-sdk
}

function get_yq() {
    release=$(curl --silent --location --insecure --fail "https://api.github.com/repos/mikefarah/yq/releases/latest" | awk '/tag_name/ { print substr($2, 3, length($2)-4) }')
    curl --silent --location --insecure --fail --output yq "https://github.com/mikefarah/yq/releases/download/v${release}/yq_linux_amd64"
    chmod a+x yq
}

function get_kubectl_directpv() {
    [ -f "${KUBECTL_DIRECTPV}" ] && return 0
    curl --silent --location --insecure --fail --output "${KUBECTL_DIRECTPV}" "https://github.com/minio/directpv/releases/download/v${BUILD_VERSION}/${KUBECTL_DIRECTPV:2}"
    chmod a+x "${KUBECTL_DIRECTPV}"
}

function init() {
    if [ "$#" -ne 2 ]; then
        cat <<EOF
USAGE:
  olm.sh <VERSION> <IMAGE-HASH>

EXAMPLE:
  $ olm.sh 4.0.6 sha256:53610c35d42971ad57ce8491dbcab3b95395a68820d9024a1298b9f653802688
EOF
        exit 255
    fi

    # assign after trimming 'v'
    BUILD_VERSION="${1/v/}"
    IMAGE_HASH="$2"
    KUBECTL_DIRECTPV="./kubectl-directpv_${BUILD_VERSION}_$(go env GOOS)_$(go env GOARCH)"

    echo "Downloading required tools"

    OPERATOR_SDK=./operator-sdk
    if which operator-sdk >/dev/null 2>&1; then
        OPERATOR_SDK=operator-sdk
    elif [ ! -f ./operator-sdk ]; then
        if ! get_operator_sdk; then
            echo "unable to get operator-sdk; exiting..."
            exit 1
        fi
    fi

    YQ=./yq
    if which yq >/dev/null 2>&1; then
        YQ=yq
    elif [ ! -f ./yq ]; then
        if ! get_yq; then
            echo "unable to get yq; exiting..."
            exit 1
        fi
    fi

    get_kubectl_directpv
}

function main() {
    mkdir -p "resources/base/${BUILD_VERSION}"
    "${KUBECTL_DIRECTPV}" install --openshift -o yaml > "resources/base/${BUILD_VERSION}/directpv.yaml"

    "${OPERATOR_SDK}" generate bundle \
                      --package "${PACKAGE}" \
                      --version "${BUILD_VERSION}" \
                      --deploy-dir "resources/base/${BUILD_VERSION}" \
                      --manifests \
                      --metadata \
                      --output-dir "bundles/redhat-marketplace/${BUILD_VERSION}" \
                      --channels stable \
                      --overwrite

    # Annotations to specify OCP versions compatibility.
    "${YQ}" -i '.annotations."com.redhat.openshift.versions" |= "v4.10-v4.13"' "bundles/redhat-marketplace/${BUILD_VERSION}/metadata/annotations.yaml"

    # Add needed annotations for redhat marketplace
    "${YQ}" -i '.metadata.annotations."marketplace.openshift.io/remote-workflow" |= "https://marketplace.redhat.com/en-us/operators/minio-directpv-operator-rhmp/pricing?utm_source=openshift_console"' "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"
    "${YQ}" -i '.metadata.annotations."marketplace.openshift.io/support-workflow" |= "https://marketplace.redhat.com/en-us/operators/minio-directpv-operator-rhmp/support?utm_source=openshift_console"' "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"

    # Use DirectPV image and hash from Red Hat registry
    sed -i -e "s/quay.io\/minio\/directpv:v.*/registry.connect.redhat.com\/minio\/directpv@${IMAGE_HASH}/g" "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"

    # To add icon right after bundle creation
    icon_
    icon_mediatype=image/png
    "${YQ}" -i ".spec.icon[0].base64data |= (\"${icon_base64data}\")" "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"
    "${YQ}" -i ".spec.icon[0].mediatype |= (\"${icon_mediatype}\")" "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"

    # To add related images after bundle creation
    image=$("${YQ}" '.spec.install.spec.deployments[0].spec.template.spec.containers[0].image' "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml")
    "${YQ}" -i ".spec.relatedImages[0].image |= (\"${image}\")" "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"
    name=$("${YQ}" '.spec.install.spec.deployments[0].spec.template.spec.containers[0].name' "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml")
    "${YQ}" -i ".spec.relatedImages[0].name |= \"${name}\"" "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"
    image=$("${YQ}" '.spec.install.spec.deployments[0].spec.template.spec.containers[1].image' "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml")
    "${YQ}" -i ".spec.relatedImages[1].image |= (\"${image}\")" "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"
    name=$("${YQ}" '.spec.install.spec.deployments[0].spec.template.spec.containers[1].name' "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml")
    "${YQ}" -i ".spec.relatedImages[1].name |= \"${name}\"" "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"
    image=$("${YQ}" '.spec.install.spec.deployments[0].spec.template.spec.containers[2].image' "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml")
    "${YQ}" -i ".spec.relatedImages[2].image |= (\"${image}\")" "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"
    name=$("${YQ}" '.spec.install.spec.deployments[0].spec.template.spec.containers[2].name' "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml")
    "${YQ}" -i ".spec.relatedImages[2].name |= \"${name}\"" "bundles/redhat-marketplace/${BUILD_VERSION}/manifests/${PACKAGE}.clusterserviceversion.yaml"
}

init "$@"
main "$@"
